hudson.tasks.Shell.command:
#!/bin/sh GROUP_ID=com/hkstlr PROJECT=text-analysis-service HOST=www.hkstlr.com NEXUS_URL=http://nexus:8081/repository/maven-releases LATEST=$(curl -s "${NEXUS_URL}/${GROUP_ID}/${PROJECT}/maven-metadata.xml" | \ grep "<release>.*</release>" | sort | uniq | tail -n1 | sed -e "s#\(.*\)\(<release>\)\(.*\)\(</release>\)\(.*\)#\3#g") VERSION=${LATEST} NEXUS_WAR_NAME=${PROJECT}-${VERSION}.war WAR_NAME=${PROJECT}.war DEPLOY_DIR=/opt/jboss/wildfly/standalone/deployments
wget ${NEXUS_URL}/com/hkstlr/${PROJECT}/${VERSION}/${NEXUS_WAR_NAME} echo "Deploying '$WAR_NAME' from '$WAR_PATH' to '$HOST'" echo '-------------------'
mv ${NEXUS_WAR_NAME} ${WAR_NAME}
echo "-> ssh ${WAR_NAME}" echo "----- scp-war ${KEYFILE} -----" scp ${WAR_NAME} ec2-user@$HOST:~/. echo "-----ssh chown mv deploy-----" ssh -T ec2-user@$HOST "sudo chown jboss:jboss ~/${WAR_NAME}" ssh -T ec2-user@$HOST "sudo mv ~/${WAR_NAME} ${DEPLOY_DIR}"
